mapper.select:
SELECT MEMBER_ID,MEMBER_NICK, MEMBER_TEL, PROFILE_IMG, MEMBER_INTRO
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.delete:
DELETE FROM FOLLOW
WHERE FOLLOWER_ID = #{followerId}
AND WRITER_ID = #{writerId}

