mapper.update:
UPDATE ADDR
SET
DELIVERY_NAME = #{deliveryName}, RECEIVER_NAME = #{receiverName}, ADDR
= #{addr}, ADDR_TEL = #{addrTel}
WHERE ADDR_ID = #{addrId}

UPDATE MEMBER SET
PROFILE_IMG = #{profileImage}
WHERE
MEMBER_ID = #{memberId}

UPDATE CART
SET QUANTITY = ${cartNum}
WHERE MEMBER_ID = #{memberId} AND PRODUCT_ID= #{productId}
